import { useEffect, useState } from 'react'
import axios from 'axios'

const ShowResults = ({results, error, showOne, setShowOne}) => {
    const handleClick = (e) => {
        console.log(e, results)
        const result = results.map(r => r.name.common === e)
        console.log(result)
        setShowOne(e)
    }

    if (error) {
        return (
            <div>
                {error}
            </div>
        )
    }
    if (results.length > 1) {
        return (
            <div>
                {results.map(r => <div key={r.name.common}>{r.name.common}<button onClick={() => {handleClick(r.name.common)}}>show</button></div>)}
            </div>
        )
    }

    if (results.length === 1) {
        const languages = results.map(r => Object.values(r.languages))
        const flag = results.map(r => r.flags.png)

        return (
            <div>
                <h1>{results.map(r => r.name.common)}</h1>
                <div>capital {results.map(r => r.capital)}</div>
                <div>area {results.map(r => r.area)}</div>
                <h3>languages: </h3>
                <ul>
                    {languages[0].map(l => <li key={l}>{l}</li>)}
                </ul>
                <img src={flag} />
            </div>
        )
    }

}


const App = () => {
    const [newValue, setNewValue] = useState('')
    const [results, setResults] = useState([])
    const [error, setError] = useState(null)
    const [showOne, setShowOne] = useState(null)

    const handleInput = (event) => {
        setNewValue(event.target.value)
        setResults([])
    }

    useEffect(() => {
        if (newValue) {
            axios
            .get(`https://restcountries.com/v3.1/all`)
            .then(response => {
                const search = response.data.filter(one => one.name.common.match(new RegExp(newValue, "i")))
                if (search.length > 10) {
                    setError('Too many matches, specify another filter')
                }
                else {
                    setResults(search)
                    setError(null)
                }
            })
        }

    }, [newValue])

    return (
        <div>
            find countries <input
            value={newValue}
            onChange={handleInput}/>
            <ShowResults results={results} error={error} showOne={showOne} setShowOne={setShowOne}/>
        </div>
    )
}


export default App
