import { useEffect, useState } from 'react'
import axios from 'axios'
const api_key = process.env.REACT_APP_API_KEY

const ShowWeather = ({lat, lon, getWeather}) => {
    return (
        <div>
            {getWeather(lat, lon)}
            {console.log('render')}
        </div>
    )
}

const ShowResults = ({results, error, showOne, setShowOne, getWeather}) => {


    useEffect(() => {
        if (showOne) {
            const result = results.filter(r => r.name.common === showOne)
            const languages = result.map(r => Object.values(r.languages))
            const flag = result.map(r => r.flags.png)
            const capital = results[0]?.name?.common
            const latlng = result.map(r => Object.values(r.capitalInfo))
            const lat = latlng[0][0][0]
            const lon = latlng[0][0][1]    
        }
    })
    /*
    if (showOne) {
        const result = results.filter(r => r.name.common === showOne)
        const languages = result.map(r => Object.values(r.languages))
        const flag = result.map(r => r.flags.png)
        const capital = result.map(r => r.capital)
        const latlng = result.map(r => Object.values(r.capitalInfo))
        const lat = latlng[0][0][0]
        const lon = latlng[0][0][1]

        return (
            <div>
                <h1>{result.map(r => r.name.common)}</h1>
                <div>capital {capital}</div>
                <div>area {result.map(r => r.area)}</div>
                <h3>languages</h3>
                <ul>
                    {languages[0].map(l => <li key={l}>{l}</li>)}
                </ul>
                <img src={flag} />
                <ShowWeather lat={lat} lon={lon} capital={capital} getWeather={getWeather}/>
            </div>
        )
    }*/

    if (error) {
        return (
            <div>
                {error}
            </div>
        )
    }
    if (results.length > 1) {
        return (
            <div>
                {results.map(r => <div key={r.name.common}>{r.name.common}<button onClick={() => {handleClick(r.name.common)}}>show</button></div>)}
            </div>
        )
    }

    if (results.length === 1) {
        setShowOne(results[0]?.name?.common)
    }

}


const App = () => {
    const [newValue, setNewValue] = useState('')
    const [results, setResults] = useState([])
    const [error, setError] = useState(null)
    const [showOne, setShowOne] = useState(null)
    const [weatherData, setWeather] = useState(null)

    const handleInput = (event) => {
        setShowOne(null)
        setNewValue(event.target.value)
        setResults([])
    }

    useEffect(() => {
        if (newValue) {
            axios
            .get(`https://restcountries.com/v3.1/all`)
            .then(response => {
                const search = response.data.filter(one => one.name.common.match(new RegExp(newValue, "i")))
                if (search.length > 10) {
                    setError('Too many matches, specify another filter')
                }
                else {
                    setResults(search)
                    setError(null)
                }
            })
        }

    }, [newValue])

    const getWeather = (lat, lon) => {
        axios
        .get(`https://api.openweathermap.org/data/2.5/weather?lat=${lat}&lon=${lon}&appid=${api_key}&units=metric`)
        .then(response => {
            const data = [response.data]
            const capital = data.map(e => e.name)
            const temperature = data.map(e => e.main.temp)
            const icon = data.map(e => e.weather[0].icon)
            const wind = data.map(e => e.wind.speed)
            const iconLink = `https://openweathermap.org/img/wn/${icon}.png`
            setWeather([capital, temperature, iconLink, wind])
        })
    }

    return (
        <div>
            find countries <input
            value={newValue}
            onChange={handleInput}/>
            <ShowResults results={results} error={error} showOne={showOne} setShowOne={setShowOne} getWeather={getWeather}/>
        </div>
    )
}

export default App
