import { useEffect, useState } from 'react'
import axios from 'axios'

const ShowResults = ({results, error}) => {
    const mapped = results.map(result => result.common)
    console.log(mapped)
    if (error) {
        return (
            <div>
                {error}
            </div>
        )
    }
    return (
        <div>
            {mapped.map(one => <div>{one}</div>)}
        </div>
    )
}


const App = () => {
    const [newValue, setNewValue] = useState('')
    const [results, setResults] = useState([])
    const [error, setError] = useState(null)

    const handleInput = (event) => {
        setNewValue(event.target.value)
        setResults([])
    }

    useEffect(() => {
        if (newValue) {
            axios
            .get(`https://restcountries.com/v3.1/all`)
            .then(response => {
                const all = response.data.map(one => one.name)
                const search = all.filter(one => one.common.match(new RegExp(newValue, "i")))
                if (search.length > 10) {
                    setError('Too many matches, specify another filter')
                }
                else {
                    setResults(search)
                    setError(null)
                }
            })
        }

    }, [newValue])

    return (
        <div>
            find countries <input
            value={newValue}
            onChange={handleInput}/>
            <ShowResults results={results} error={error}/>
        </div>
    )
}


export default App
