import { useEffect, useState } from 'react'
import axios from 'axios'

const ShowResults = ({results, error}) => {
    if (error) {
        return (
            <div>
                {error}
            </div>
        )
    }
    if (results.length > 1) {
        return (
            <div>

            </div>
        )
    }
    if (results.length === 1) {
        return (
            <div>
                <h1>pää</h1>
                <div>capital</div>
                <div>area</div>
                <h3>languages:</h3>
            </div>
        )
    }

}


const App = () => {
    const [newValue, setNewValue] = useState('')
    const [results, setResults] = useState([])
    const [error, setError] = useState(null)

    const handleInput = (event) => {
        setNewValue(event.target.value)
        setResults([])
    }

    useEffect(() => {
        if (newValue) {
            axios
            .get(`https://restcountries.com/v3.1/all`)
            .then(response => {
                const all = response.data.map(one => one)
                const search = response.data.filter(one => one.name.common.match(new RegExp(newValue, "i")))
                if (search.length > 10) {
                    setError('Too many matches, specify another filter')
                }
                else {
                    setResults(search)
                    setError(null)
                }
            })
        }

    }, [newValue])

    return (
        <div>
            find countries <input
            value={newValue}
            onChange={handleInput}/>
            <ShowResults results={results} error={error}/>
        </div>
    )
}


export default App
